sys_script_client.script:
function onChange(control, oldValue, newValue, isLoading, isTemplate) {
   if (isLoading || newValue === '') {
      return;
   }
	if(newValue != ""){
		g_form.getReference('user_id',getUserData);
	}

	function getUserData(resp){
		g_form.setValue('name',resp.name);
		g_form.setValue('email_id',resp.email);
		g_form.setValue('mobile_number',resp.mobile_phone);
		g_form.setValue('is_vip',resp.vip);

	}
   //Type appropriate comment here, and begin script below

}
