sys_script_client.script:
function onChange(control, oldValue, newValue, isLoading, isTemplate) {
   if (newValue === '') {
      return;
   }
	if(String(newValue) == "true"){
		g_form.setMandatory('user_id',false);
		g_form.setDisplay('user_id',false);
		g_form.setReadOnly('name',false);
		g_form.setReadOnly('email_id',false);
		g_form.setReadOnly('mobile_number',false);
		g_form.setMandatory('name',true);
		g_form.setMandatory('mobile_number',true);
		g_form.setMandatory('vehicle_number',true);
		g_form.setValue('user_id',"");
		g_form.setValue('name',"");
		g_form.setValue('email_id',"");
		g_form.setValue('mobile_number',"");
		g_form.setValue('vehicle_number',"");
		g_form.setValue('is_vip',false);

	}else{
		g_form.setDisplay('user_id',true);
		g_form.setMandatory('user_id',true);
		g_form.setMandatory('name',false);
		g_form.setMandatory('mobile_number',false);
		g_form.setMandatory('vehicle_number',true);
		g_form.setReadOnly('name',true);
		g_form.setReadOnly('email_id',true);
		g_form.setReadOnly('mobile_number',true);


		g_form.setValue('name',"");
		g_form.setValue('email_id',"");
		g_form.setValue('mobile_number',"");
		g_form.setValue('vehicle_number',"");


	}


   //Type appropriate comment here, and begin script below

}
