sys_ui_page.client_script:
function getParkingDetails(){
	var user_id = gel('user_id').value;
	var ga = new GlideAjax('ParkingUtils');
	ga.addParam('sysparm_name','logOutParking');
	ga.addParam('sysparm_user',user_id);
	ga.getXML(parkingCallback);
}
function parkingCallback(response){
	var answer = response.responseXML.documentElement.getAttribute("answer");
	var obj = JSON.parse(answer);
	if(obj.slot == ''){
		gel('error').innerHTML = obj.msg; 
		gel('slot').innerHTML = '';
		gel('output').innerHTML = '';
	}else{
		gel('error').innerHTML = ''; 
		gel('slot').innerHTML = obj.slot;
		gel('output').innerHTML = "Thank you! Name: "+obj.name+"<br>Entry Time: "+obj.entry_time+"<br>Exit Time: "+obj.exit_time+"<br>Total Duration: "+obj.duration+"<br>Vehicle Number: "+obj.vehicle_number;
	}

}
